import React from 'react';

const logoDataUri = "data:image/svg+xml;base64,";

export const LogoIcon = (props: React.ImgHTMLAttributes<HTMLImageElement>) => {
  const { className, ...rest } = props;
  return (
    <img
      src={logoDataUri}
      alt="Class Captain Logo"
      className={className}
      {...rest}
    />
  );
};